let e=!1;
const l=document.querySelector("#navLinks"),
    t=document.querySelector("#hamburger"),
    s=document.querySelector("#navLayer"),
    i=[...l.querySelector("ul").children];

function a(){
    e?(
    l.classList.add("!visible","!scale-100","!opacity-100","!md:translate-y-0"),
        t.classList.add("toggled"),s.classList.remove("origin-bottom","scale-y-0"),
        s.classList.add("origin-top","scale-y-100"))
        :
        (l.classList.remove("!visible","!scale-100","!opacity-100","!md:translate-y-0"),
    t.classList.remove("toggled"),
    s.classList.remove("origin-top","scale-y-100"),
    s.classList.add("origin-bottom","scale-y-0"))
}

t.addEventListener("click",()=>{e=!e,a()});
i.forEach(c=>{c.addEventListener("click",()=>{e=!e,a()})});
